mapper.select:
 select u.user_id,u.real_name, u.dept_id, u.user_name,u.merchant_id, u.nick_name, u.email, u.avatar, u.phonenumber, u.sex, u.password,
 u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark
  from sys_user u
 where u.phonenumber = #{username}

       and u.merchant_id=#{merchantId}

and del_flag='0'

mapper.update:
update sys_user
dept_id = #{deptId}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, del_flag = #{delFlag}, grade = #{grade}, update_time = sysdate()
where user_id = #{userId}

mapper.select:
SELECT max( a.user_id ) AS user_id, max( b.role_id ) AS role_id FROM sys_user_role AS a LEFT JOIN sys_role as b ON b.role_id = a.role_id WHERE b.merchant_id = #{merchantId}
AND b.role_id in
#{item}
GROUP BY a.user_id

SELECT DISTINCT u.* from sys_user u left JOIN sys_user_role ur on ur.user_id=u.user_id left JOIN sys_role r on r.role_id =ur.role_id
and r.role_type in
#{roleId}
and u.merchant_id = #{merchantId}
and u.del_flag='0' order by u.create_time desc

SELECT u.user_id AS userId, u.user_name AS userName, u.dept_id detpId, u.status status, u.phonenumber phoneNumber, r.role_id AS roleId, r.role_name AS roleName, r.role_key AS roleKey, r.role_type AS roleType FROM `sys_user_role` ur LEFT JOIN sys_user u ON ur.user_id = u.user_id LEFT JOIN sys_role r ON ur.role_id = r.role_id WHERE 1 = 1
AND u.merchant_id = #{merchantId}
AND r.role_type = #{roleType}

SELECT DISTINCT u.* from sys_user u
 left JOIN sys_user_role ur on ur.user_id=u.user_id
 left JOIN sys_role r on r.role_id =ur.role_id


         and r.role_id = #{roleType}


         and u.merchant_id = #{merchantId}


         and u.user_name like concat('%',#{userName},'%')


         and u.dept_id in

                 #{item}



         and u.status = #{status}


         and u.phonenumber like concat('%',#{phonenumber},'%')


         and u.create_time > #{beginDate}


         and u.create_time < #{endDate}


 and u.del_flag='0'
 order by u.create_time desc,u.user_name desc

SELECT DISTINCT u.* from sys_user u
left JOIN sys_user_role ur on ur.user_id=u.user_id
left JOIN sys_role r on r.role_id =ur.role_id


        and r.role_id = #{roleType}


        and u.merchant_id = #{merchantId}


        and u.user_name like concat('%',#{userName},'%')


        and u.dept_id in

            #{item}



        and u.status = #{status}


        and u.phonenumber like concat('%',#{phonenumber},'%')


        and u.create_time > #{beginDate}


        and u.create_time < #{endDate}


and u.del_flag='0' and u.user_id=#{userId}
order by u.create_time desc,u.user_name desc

mapper.update:
update sys_user
dept_id = #{deptId}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, del_flag = #{delFlag}, grade = #{grade}, update_time = sysdate()
where user_id = #{userId}
